mapper.select:
SELECT COUNT(*)
FROM tblBlock

	intStatus = 1

		AND strBlockDesc LIKE #{strBlockName}


		AND blockId = #{blockId}

SELECT b.*, u.*, uc.intLevelNo FROM tblBlock b LEFT JOIN tblUnit u ON b.blockId = u.blockIdFK LEFT JOIN tblUnitCategory uc ON uc.unitCategoryId = u.unitCategoryIdFK
intStatus = 1
AND b.strBlockDesc LIKE #{strBlockName}
AND b.blockId = #{blockId}
