mapper.select:
SELECT COUNT(*)
FROM tblItem

	intStatus = 1 

		AND strItemName LIKE #{strItemName}


		AND itemId = #{itemId}

SELECT COUNT(*)
FROM tblItemCategory
WHERE strItemCategoryDesc = #{strItemCategoryDesc}

SELECT i.itemId, i.strItemName, i.strItemDesc, i.deciItemPrice, ic.* FROM tblItem i INNER JOIN tblItemCategory ic ON i.itemCategoryIdFK = ic.itemCategoryId
i.intStatus = 1
AND i.itemId = #{itemId}
AND i.strItemName LIKE #{strItemName}
LIMIT 1

SELECT i.itemId, i.strItemName, i.strItemDesc, i.deciItemPrice, ic.* FROM tblItem i INNER JOIN tblItemCategory ic ON i.itemCategoryIdFK = ic.itemCategoryId
i.intStatus = 1
AND i.strItemName LIKE #{itemName}
AND i.itemId = #{itemId}
ORDER BY strItemName ASC

mapper.update:
UPDATE tblItem

	strItemName = #{strItemName},
	strItemDesc = #{strItemDesc},
	deciItemPrice = #{dblPrice}

WHERE itemId = #{itemId}
