mapper.select:

SELECT e.employeeId, e.strFirstName, e.strMiddleName, e.strLastName,
	e.datBirth, e.strGender, e.strCivilStatus, p.positionId, p.strPositionDesc
FROM tblEmployee e
	INNER JOIN tblPosition p ON e.positionIdFK = p.positionId

	e.intStatus = 1

		AND e.employeeId = #{employeeId}


		AND e.strFirstName = #{name.strFirstName}
		AND e.strMiddleName = #{name.strMiddleName}
		AND e.strLastName = #{name.strLastName}


LIMIT 1

SELECT COUNT(*)
FROM tblEmployee
WHERE intStatus = 1
	AND strFirstName = #{name.strFirstName}
	AND strMiddleName = #{name.strMiddleName}
	AND strLastName = #{name.strLastName}

SELECT COUNT(*)
FROM tblPosition
WHERE strPositionDesc = #{strPositionDesc}

SELECT * FROM tblPosition
WHERE strPositionDesc = #{strPositionDesc}
LIMIT 1

mapper.update:
UPDATE tblEmployee
strPassword = MD5(#{strPassword}), strFirstName = #{name.strFirstName}, strMiddleName = #{name.strMiddleName}, strLastName = #{name.strLastName}, datBirth = #{birthday}, strGender = #{strGender}, strCivilStatus = #{strCivilStatus}, positionIdFK = #{position.positionId}
WHERE employeeId = #{employeeId}
