mapper.select:
SELECT COUNT(*)
FROM tblService

	intStatus = 1


			OR serviceId = #{serviceId}


			OR strServiceName LIKE #{strServiceName}

SELECT s.*, r.* FROM tblService s LEFT JOIN tblServiceRequirement sr ON s.serviceId = sr.serviceIdFK LEFT JOIN tblRequirement r ON r.requirementId = sr.requirementIdFK
s.intStatus = 1 AND sr.intStatus = 1 AND r.intStatus = 1
AND serviceId = #{serviceId}
AND strServiceName LIKE #{strServiceName}

SELECT s.serviceId, s.strServiceName, s.strServiceDesc, s.deciPrice, r.* FROM tblService s LEFT JOIN tblServiceRequirement sr ON s.serviceId = sr.serviceIdFK LEFT JOIN tblRequirement r ON r.requirementId = sr.requirementIdFK
s.intStatus = 1 AND sr.intStatus = 1 AND r.intStatus = 1
AND s.strServiceName LIKE #{serviceName}
AND s.serviceId = #{serviceId}

SELECT COUNT(*) FROM tblServiceRequirement
WHERE serviceIdFK = #{serviceId}
	AND requirementIdFK = #{requirementId}

mapper.update:
UPDATE tblService

	strServiceName = #{strServiceName},
	strServiceDesc = #{strServiceDesc},
	deciPrice = #{dblPrice}

WHERE serviceId = #{serviceId}

UPDATE tblServiceRequirement
SET intStatus = 0
WHERE serviceIdFK = #{serviceId}
	AND requirementIdFK = #{requirementId}

UPDATE tblServiceRequirement
SET intStatus = 1
WHERE serviceIdFK = #{serviceId}
	AND requirementIdFK = #{requirementId}
